<?php//========================//if(INCLUDED!==true) {	echo "Not Included!"; exit;}//=======================//$realms = $DB->select("SELECT * FROM realmlist ORDER BY `id` ASC");function saveConfig() {	global $Config, $lang;	foreach ($_POST as $item => $val) 	{		$key = explode('__', $item);		if ($key[0] == 'cfg') 		{			$Config->set($key[1],$val);		}	}	$Config->Save();		output_message('success', $lang['config_updated_successfully'].'<meta http-equiv=refresh content="3;url=?p=admin&sub=siteconfig">');}?>